function switchLanguage(e){document.getElementById("content-en").classList.toggle("hidden",e!=="en"),document.getElementById("content-jp").classList.toggle("hidden",e!=="jp"),document.documentElement.lang=e}document.querySelectorAll(".language-switch a").forEach(e=>{e.addEventListener("click",t=>{t.preventDefault();const n=e.dataset.lang;switchLanguage(n)})});
